import React from 'react';
import './App.css';
import CatList from './CatList';
import CatDetail from './CatDetail';
import CatForm from './CatForm';
import CatGallery from './CatGallery';

class App extends React.Component{

  constructor(props)
  {
    super(props);
    this.state={
        cats:[
              {id:1 ,counter:0, name: 'Cat 1', data:'Ginger',age:'infant' ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
              {id:2, counter:0 ,name: 'Cat 2', data:'Brown',age:'infant' ,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'},
              {id:3, counter:0 ,name: 'Cat 3', data:'White',age:'infant' ,url:'https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn-images-1.medium.com%2Fmax%2F1600%2F1*mONNI1lG9VuiqovpnYqicA.jpeg&imgrefurl=https%3A%2F%2Fhackernoon.com%2Fa-guide-to-giving-your-cats-their-annual-performance-review-fbf14610305&docid=j3cqwRg1oS8drM&tbnid=KVd44jdPGcx-NM%3A&vet=10ahUKEwi-qdaT-O_hAhXBs48KHQVbA5gQMwhwKAcwBw..i&w=1600&h=911&bih=789&biw=1440&q=cats&ved=0ahUKEwi-qdaT-O_hAhXBs48KHQVbA5gQMwhwKAcwBw&iact=mrc&uact=8'},
              {id:4 ,counter:0, name: 'Cat 1', data:'Ginger',age:'infant' ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
              {id:5, counter:0 ,name: 'Cat 2', data:'Brown',age:'infant' ,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'},
        ],
        selectedCat: {id:1 ,counter:0, name: 'cat1', data:'small',age:2 ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'}
    }
  }

  selectACat = (id) => {
    let searchedCat = this.state.cats.filter(cat => cat.id == id);
    this.setState({
      selectedCat: searchedCat[0]
    })
  }

  increment = () => {
    let newSelectedCat = this.state.selectedCat;
    let newCatState = this.state.cats;
    newSelectedCat.counter += 1;

    if(newSelectedCat.counter >= 6 && newSelectedCat.counter <= 12) {
      newSelectedCat.age = 'Child'
    }
    if(newSelectedCat.counter >= 13 && newSelectedCat.counter <= 25) {
      newSelectedCat.age = 'Young'
    }
    if(newSelectedCat.counter >= 26 && newSelectedCat.counter <= 40) {
      newSelectedCat.age = 'Middle-Age'
    }
    if(newSelectedCat.counter >= 41 && newSelectedCat.counter <= 60) {
      newSelectedCat.age = 'Old'
    }
    if(newSelectedCat.counter >= 61) {
      newSelectedCat.age = 'Very Old'
    }

    let index = newCatState.findIndex(cat => cat.id == newSelectedCat.id);
    newCatState[index].counter = newSelectedCat.counter;
    newCatState[index].age = newSelectedCat.age;
    this.setState({
      cats: newCatState,
      selectedCat: newSelectedCat
    })
  }

  render(){
    console.log(this.state.selectedCat)
  return (
    <div className="App">
      <header>
          <p className="Title">
            Cat Clicker App
          </p>
      </header>
      <div className="Panels">
        <CatList cats={this.state.cats} selectACat={this.selectACat} />
        <CatDetail selectedCat={this.state.selectedCat} Increment={this.increment} />
        <CatForm/>
      </div>
      <div>
        <CatGallery catList={this.state.cats}/>
      </div>
    </div>
  );
}
}

export default App;
