import React from 'react';
import './App.css';
import RightPanel from './RightPanel';
import MidPanel from './MidPanel';
import LeftPanel from './LeftPanel';


var cats=[
{id:1 ,counter:0, name: 'cat1', data:'small',age:2 ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
{id:2, counter:0 ,name: 'cat2', data:'big',age:1,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'}];


class App extends React.Component{

  constructor(props)
  {
    super(props);
    this.state={
        cats:[
              {id:1 ,counter:0, name: 'cat1', data:'small',age:2 ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
              {id:2, counter:0 ,name: 'cat2', data:'big',age:1 ,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'}
        ],
        selectedCat: {id:1 ,counter:0, name: 'cat1', data:'small',age:2 ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'}
    }
  }

  selectACat = (id) => {
    let searchedCat = this.state.cats.filter(cat => cat.id == id);
    this.setState({
      selectedCat: searchedCat[0]
    })
  }

  increment = () => {
    let newSelectedCat = this.state.selectedCat;
    newSelectedCat.counter += 1;
    let newCatState = this.state.cat;
    let index = newCatState.findIndex(cat => cat.id == newSelectedCat.id);
    newCatState[index].counter += 1;
    this.setState({
      cats: newCatState,
      selectedCat: newSelectedCat
    })
  }

  render(){
    console.log(this.state.selectedCat)
  return (
    <div className="App">
      <header>
          <p className="Title">
            Cat Clicker App
          </p>
      </header>
      <div className="Panels">
        <LeftPanel cats={this.state.cats} selectACat={this.selectACat} />
        <MidPanel selectedCat={this.state.selectedCat} Increment={this.increment} />
        <RightPanel/>
      </div>
    </div>
  );
}
}

export default App;
