import React from 'react';
import './App.css';
import RightPanel from './RightPanel';
import MidPanel from './MidPanel';
import LeftPanel from './LeftPanel';


var cats=[
{id:1 ,counter:0, name: 'cat1', data:'small' ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
{id:2, counter:0 ,name: 'cat2', data:'big' ,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'}];


class App extends React.Component{

  constructor(props)
  {
    super(props);
    this.state={
        cats:[
              {id:1 ,counter:0, name: 'cat1', data:'small' ,url:'http://cdn3-www.cattime.com/assets/uploads/2011/08/best-kitten-names-1.jpg'},
              {id:2, counter:0 ,name: 'cat2', data:'big' ,url:'http://inspirationseek.com/wp-content/uploads/2016/02/Cute-Kitten-Pics_32.jpg'}
        ],
        selectedCat: {}
    }
  }

  selectACat = (id) => {
    let searchedCat = this.state.cats.filter(cat => cat.id == id);
    console.log(searchedCat)
    this.setState({
      selectACat: searchedCat
    })
    console.log(this.state.selectedCat)
  }

  render(){
  return (
    <div className="App">
      <header>
          <p className="Title">
            Cat Clicker App
          </p>
      </header>
      <div className="Panels">
        <LeftPanel cats={this.state.cats} selectACat={this.selectACat} />
        <MidPanel />
        <RightPanel/>
      </div>
    </div>
  );
}
}

export default App;
