import React from 'react';
import './LeftPanel.css';
import {Button,Badge} from 'react-bootstrap';

class LeftPanel extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			selectedButton: 0
		}
	}

	handleClick = (e, id) => {
		this.props.selectACat(id);
		this.setState({
			selectedButton: id
		})
	}

	render() {
		let catsBar = this.props.cats.map((cat, index) => {
			return <Button key={index} variant={cat.id == this.state.selectedButton ? 'primary' : 'light'}  size="lg" block onClick={(e) => this.handleClick(e, cat.id)}>
				 	{cat.name} <Badge pill variant="secondary">{cat.counter}</Badge>
				  </Button>
		})
		return(
			 <div className="LeftPanel">
			 	{catsBar}
	        </div>
		);
	}
}


export default LeftPanel;
